package com.example.dan.mommarket.adapter;import android.content.Context;import android.support.v7.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.TextView;import com.example.dan.mommarket.R;import com.example.dan.mommarket.model.OfferItem;import com.squareup.picasso.Picasso;import java.util.List;/** * Created by dan on 27.08.16. */public class CartShopOfferItemAdapter extends RecyclerView.Adapter<CartShopOfferItemAdapter.ViewHolder> {    private List<OfferItem> offerItemList;    private Context context;    private OnOfferItemListener onOfferItemListener;    public CartShopOfferItemAdapter(List<OfferItem> offerItemList, Context context, OnOfferItemListener listener) {        this.offerItemList = offerItemList;        this.onOfferItemListener = listener;        this.context = context;    }    public interface OnOfferItemListener {        void onItemClick(int item);    }    @Override    public ViewHolder onCreateViewHolder(ViewGroup parent, int viewType) {        View v = LayoutInflater.from(parent.getContext()).inflate(R.layout.cart_shop_offer_item, parent, false);        return new ViewHolder(v, onOfferItemListener);    }    @Override    public void onBindViewHolder(ViewHolder holder, int position) {        OfferItem offerItem = offerItemList.get(position);        holder.name.setText(offerItem.getProduct().getName());        holder.price.setText(String.valueOf(offerItem.getOffer().getPrice()));        Picasso.with(context).load(offerItem.getProduct().getFirstImage()).into(holder.image);    }    @Override    public int getItemCount() {        return offerItemList.size();    }    public class ViewHolder extends RecyclerView.ViewHolder implements View.OnClickListener {        private TextView name;        private TextView price;        private ImageView image;        private OnOfferItemListener listener;        public ViewHolder(View itemView, OnOfferItemListener listener) {            super(itemView);            name = (TextView) itemView.findViewById(R.id.cart_shop_offer_item_name);            price = (TextView) itemView.findViewById(R.id.cart_shop_offer_item_price);            image = (ImageView) itemView.findViewById(R.id.cart_shop_offer_item_image);            this.listener = listener;            itemView.setOnClickListener(this);        }        @Override        public void onClick(View view) {            listener.onItemClick(offerItemList.get(getAdapterPosition()).getId());        }    }}