package com.example.dan.mommarket.adapter;import android.content.Context;import android.support.v7.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.TextView;import com.example.dan.mommarket.R;import com.example.dan.mommarket.model.Advice;import com.example.dan.mommarket.model.OfferItem;import java.util.List;/** * Created by dan on 27.08.16. */public class CartShopOfferItemAdapter extends RecyclerView.Adapter<CartShopOfferItemAdapter.ViewHolder> {    private List<OfferItem> offerItemList;    private Context context;    public CartShopOfferItemAdapter(List<OfferItem> offerItemList) {        this.offerItemList = offerItemList;        this.context = context;    }    @Override    public ViewHolder onCreateViewHolder(ViewGroup parent, int viewType) {        View v = LayoutInflater.from(parent.getContext()).inflate(R.layout.cart_shop_offer_item, parent, false);        return new ViewHolder(v);    }    @Override    public void onBindViewHolder(ViewHolder holder, int position) {        OfferItem offerItem = offerItemList.get(position);        holder.name.setText(offerItem.getProduct().getName());    }    @Override    public int getItemCount() {        return offerItemList.size();    }    public class ViewHolder extends RecyclerView.ViewHolder {        private TextView name;        public ViewHolder(View itemView) {            super(itemView);            name = (TextView) itemView.findViewById(R.id.cart_shop_offer_item_name);        }    }}